@file:Suppress("DEPRECATION")

package me.weishu.reflection

import android.content.Context
import android.os.Build
import android.os.Build.VERSION
import android.util.Base64
import dalvik.system.DexFile
import java.io.File
import java.io.FileOutputStream

/**
 * @author weishu
 * @date 2018/6/7
 * @author AbandonedCart
 * @date 2023/1/31
 */
object Reflection {
    private const val DEX = ""

    private external fun unsealNative(targetSdkVersion: Int): Int

    @JvmStatic
    fun unseal(context: Context): Int {
        // Below Android P, ignore
        if (VERSION.SDK_INT < 28) return 0

        // try exempt API first.
        if (BootstrapClass.exemptAll()) return 0
        return if (unsealByDexFile(context)) 0 else -1
    }

    private fun unsealByDexFile(context: Context): Boolean {
        val bytes = Base64.decode(DEX, Base64.NO_WRAP)
        val codeCacheDir = getCodeCacheDir(context) ?: return false
        val code = File(codeCacheDir, System.currentTimeMillis().toString() + ".dex")
        return try {
            FileOutputStream(code).use { fos -> fos.write(bytes) }
            val dexFile = DexFile(code)
            // This class is hardcoded in the dex, Don't use BootstrapClass.class to reference it
            // it maybe obfuscated!!
            val bootstrapClass = dexFile.loadClass("me.weishu.reflection.BootstrapClass", null)
            val exemptAll = bootstrapClass.getDeclaredMethod("exemptAll")
            exemptAll.invoke(null) as Boolean
        } catch (e: Throwable) {
            e.printStackTrace()
            false
        } finally {
            if (code.exists()) code.delete()
        }
    }

    private fun getCodeCacheDir(context: Context?): File? {
        if (VERSION.SDK_INT >= Build.VERSION_CODES.LOLLIPOP && null != context)
            return context.codeCacheDir
        val tmpDir = System.getProperty("java.io.tmpdir")
        if (tmpDir.isNullOrEmpty()) return null
        val tmp = File(tmpDir)
        return if (!tmp.exists()) null else tmp
    }
}